source.query:
PREFIX db: <http://dbmi-icode-01.dbmi.pitt.edu:2020/resource/>
PREFIX dikbD2R: <http://dbmi-icode-01.dbmi.pitt.edu:2020/vocab/resource/>
PREFIX owl: <http://www.w3.org/2002/07/owl#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
CONSTRUCT {
  ?s a <http://bio2rdf.org/drugbank_vocabulary:Drug-Drug-Interaction>.
  ?s <http://www.w3.org/2000/01/rdf-schema#label> ?desc.
  ?drug1 <http://bio2rdf.org/drugbank_vocabulary:ddi-interactor-in> ?s.
  ?drug2 <http://bio2rdf.org/drugbank_vocabulary:ddi-interactor-in> ?s.
}
WHERE {
  ?s <http://www.w3.org/2000/01/rdf-schema#label> ?desc.
  ?drug1 owl:sameAs <http://bio2rdf.org/drugbank:$drug1>.
  ?drug2 owl:sameAs <http://bio2rdf.org/drugbank:$drug2>.
  {
    ?s dikbD2R:ObjectDrugOfInteraction ?drug1.
    ?s dikbD2R:PrecipitantDrugOfInteraction ?drug2.
  } UNION {
    ?s dikbD2R:ObjectDrugOfInteraction ?drug2.
    ?s dikbD2R:PrecipitantDrugOfInteraction ?drug1.
  }
}
